%% Forex downloading task

% Create a database object
d = dbEUROSTAT();

% Fetch EUROSTAT table of contents
toc = TOC(d);

% [1] Select Eurostat table
% -> table definition generated by picktable(d) command
d.table = toc.data.economy.ert.ert_bil.ert_bil_eur.ert_bil_eur_m;

% [2] Apply filtering criteria
% -> the result of tsobj(d) command with empty d.filter=''
filt = struct();
filt.statinfo = 'AVG'; % Average
filt.unit = 'NAC'; % National currency
filt.currency = {'CZK';'HUF';'PLN'};

d.filter = filt;

% Download selected data
t = tsobj(d);

plot(t);


